mapper.update:
update setmeal
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id};

mapper.select:
select s.*,c.name as categoryName from setmeal s,category c where s.category_id = c.id
    and category_id = #{categoryId}
    and name = #{name}
    and status = #{status}
order by create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
